:root{
    --primary-color: #2584ff;
    --secondary-color:#00d9ff;
    --accent-color:#ff3400;
    --color-headings: #1b0760;
    --color-body: #918ca4;
    --color-body-dark: #5c5577;
    --border-color:#ccc;
    --border-radius:30px;
}

*,
*::after,
*::before {
  box-sizing: border-box;
}

/*typography*/
html{
    font-size: 62.5%;
   

}
body{
    font-family: 'Inter', Arial, Helvetica, sans-serif ;
  ;
    color: var(--color-body);
    font-size: 2.4rem;
    line-height: 1.5;
}
h1,h2,h3{
 
   
  margin-bottom: 1rem;
  line-height: 1.1;
}
h1{
   
    font-size: 7rem;
}
h2{
    font-size: 4rem;

}
h3{
    font-size: 3rem;
    font-weight: 500;
}
p{
    margin-top: 0;
}

@media screen and (min-width:1024) {

    body{
        font-size: 1.8rem;
    }
    h1{
      font-size: 8rem;
    }
    h2{
        font-size: 4rem;
        color: white;
    }
    h3{
        font-size: 2.4rem;
    }
    
}
/* links */
a{
    text-decoration: none;
}
.arow-learn{
    font-size: 2rem;
    color: var(--accent-color);
    text-transform: uppercase;
    text-decoration: none;
    font-weight: bold;
  

}
.arow-learn::after{
    content:'-->';
    margin-left: 5px;
    transform: margin 0.15s ;
}
.arow-learn:hover::after{
    margin-left: 10px;
}

@media screen and (min-width: 1024px) {
    .arow-learn{
        font-size: 1.5rem;
    }
}
/*Badges*/

.badge{
    border-radius: 20px;
    font-size: 2rem;
    font-weight: 600;
    padding: 0.5rem 2rem;
    white-space: nowrap;
    text-transform: uppercase;
}


.badge--primary{
    color: white;
    background-color: var(--primary-color); 
}
.badge--secondary{
    color: white;
    background-color: var(--secondary-color); 
}
.badge--small{
    font-size: 1.6rem;
}
@media  screen and (min-width:1024px) {
    .badge{
        font-size: 1.5rem;
    }
    .badge--small{
        font-size: 1.3rem;
    }
    
}
/* lists */

.list{
    list-style: none;

    padding-left: 0rem;
   
}
.list--inline li{
    display: inline-block;
   margin-right: 2rem;
}
.list--tick{
    list-style-image: url(../images/tick.svg);
    margin-left: 2rem;
    color: var(--color-headings);

}
.list--tick, .list__item{
    padding-left: 2rem;
    margin-bottom: 1rem;
}

@media screen and (min-width:1024px) {
.list--tick li{
padding-left: 0;
}
    
}

/* icons */
.icon{
    width: 40px;
    height: 40px;
  
}
.icon--small{
    width: 30px;
    height: 30px;
}
.icon--primary{

    fill: var(--primary-color);
}
.icon--white{
 
fill: white;
}
.icon-container{
    background-color: #f3f9fa;
    width: 64px;
    height: 64px;
    border-radius: 100%;
    display: inline-flex;
    justify-content: center;
    align-items: center;

}
.icon--accent{
    background: var(--accent-color);
}

/* Buttons */
.btn{
border-radius: 40px;
border: 0;
cursor: pointer;
font-size: 1.8rem;
font-weight: 600;
margin: 1rem 0;
padding: 2rem 4vw;
outline: 0;

text-align: center;
text-transform: uppercase;
white-space: nowrap;
}
.btn .icon{
    width: 2rem;
    height: 2rem;
    margin-right: 1rem;
    vertical-align: middle;
    box-sizing: border-box;
    justify-content: flex-end;
}
.btn--primary{
    background-color: var(--primary-color);
    color: white; 
}

.btn--primary:hover{
    background-color:#3a8ffd ;
}

.btn--secondary{
 
    background-color: var(--secondary-color);
    color: white;
}
.btn--secondary:hover{
    background-color: #05cdf0;
}
.btn--accent{
    background-color: var(--accent-color);
    color: white;
}
.btn--accent:hover{
    background-color: #ec3000; 
}
.btn--outline{
    background-color: white;
    color: var(--color-headings);
    border: 2px solid black;
}
.btn--outline:hover{
    background-color: var(--color-headings);
    color: white;
}
.btn--streched{
    padding-right: 6rem;
    padding-left: 6rem;
}
.btn--block{
    width: 100%;
    display: inline-block;
}
@media screen and (min-width:1024) {
    .btn{
        font-size: 1.5rem;
    }

   
}
 /* input */
 .input{
    border-radius: var(--border-radius);
    border: 1px solid var(--border-color) ;
    color: var(--color-headings);
    font-size: 2rem;
    outline: 0;
    padding: 1.5rem 3.5rem;
}
::placeholder{
    color:  #cdcbd7;
}
.input--group{
    border: 1px solid var(--border-color);
    border-radius: var(--border-radius);
    display: flex;
}
.input--group .input{

    border: 0;
    flex-grow: 1;
    padding: 1.5rem 2rem;
    box-sizing: border-box;
    width: 0;

}
.input--group .btn{
    margin:4px;
    box-sizing: border-box;

}
@media screen and (min-width:1024) {
    .input{
        font-size: 1.5rem;
    }
}
/* Cards */

.card{
border-radius: 7px;


box-shadow: 0 0 10px 20px #f3f3f3;
overflow: hidden;
transition: transform .2s ease-out;
}
.card__heading,.card__body{

    padding: 2rem 3rem;
}
.card--primary .card__heading{
    color: white;
    background-color: var(--primary-color);
   

}
.card--secondary .card__heading{
    color: white;
    background-color: var(--secondary-color);
}
.card--primary .badge--primary{
    background: #126de4;
}

.card--secondary .badge--secondary {
    background: #02cdf1;
  }

  .card:hover{
    transform: scale(1.05);
  }


/* Plans */
.plan__name {
    color: #fff;
    margin: 0;
    font-weight: 500;
    font-size: 2.4rem;
  }
  
  .plan__price {
    font-size: 5rem;
  }
  
  .plan__duration {
    font-size: 2rem;
    font-weight: 300;
    opacity: 0.8;
    margin-right: 1rem;
  }
  
  .plan__description {
    font-size: 2rem;
    font-weight: 300;
    letter-spacing: 1px;
    display: block;
  }
  
  .plan .list__item {
    margin-bottom: 2rem;
  }
  
  .popular .card__heading {
    position: relative;
  }
  
  .popular .card__heading::before {
    content: url(../images/popular.svg);
    width: 40px;
    display: inline-block;
    position: absolute;
    top: -6px;
    right: 5%;
  }

  .popular{
    transform: scale(1.1);
  }
  .popular:hover{
    transform: scale(1.15);
  }
  
  @media screen and (min-width: 1024px) {
    .plan__name {
      font-size: 1.4rem;
    }
  
    .plan__price {
      font-size: 5rem;
    }
  
    .plan__duration {
      font-size: 1.6rem;
    }
  
    .plan__description {
      font-size: 1.7rem;
    }
  }
  

/* Media */
.media {
    display: flex;
    margin-bottom: 4rem;
   
  }
  
  .media__title {
    margin-top: 0;
    color: white;
  
  }
  
  .media__body {
    margin:0rem 2rem;
  }
  
  .media__image {
    margin-top: 1rem;
  }
  .media .icon{
    justify-content: center;
  }
  
  /* quotes */
  .quote{
    font-size: 3rem;
    color: var(--color-body-dark);
    font-style: italic;
    line-height: 1.3;
  }
  .quote__body::before{
    content: open-quote;

  }
  .quote__body::after{
    content: close-quote;
  }
  .own__name{
    margin-top: 0;
    font-size: 3rem;
    font-weight: 500;
    margin-bottom: 0;
    font-style: normal;
  }
  .own{
    color: var(--color-headings);
    font-size: 2rem;
    font-style: normal;
   
    opacity: 0.4;
  }
.quote__line{

    position: relative;
    bottom: 10px;


}

@media screen and (min-width:1024px) {
    .quote{
        font-size: 2rem;
    }
    .own__name{
        font-size: 2.4rem;
    }
    .own{
        font-size: 1.6rem;
    }
}

/* Grids */

.grid{
    display: grid;
}

@media screen and (min-width:768px) {
 .grid--1x2{
    grid-template-columns: repeat(2,1fr);
 }
    
}

@media screen and (min-width:1024px) {
    .grid--1x3{
        grid-template-columns: repeat(3,1fr);
    }
}

/* testimonial */
.test{
    padding: 3rem;

}
.test__image{
    position: relative;
    margin: 0 3rem;
}
.test__image > img{
    width: 100%;
}

.test__image > .icon-container{
position: absolute;
top: 3rem;
right: -32px;

}

@media screen and (min-width: 768px) {
    .test__image{
        margin: 0;
    }
    .test .quote,.own__name{
        font-size: 2.4rem;
    }
    .test .quote{
        margin-left: 6rem;
        margin-right: 0;
    }
}

/* Callout */


.callout{
 
    padding: 4rem;
    border-radius: 5px;
   
}
 .callout--primary{
    background-color: var(--primary-color);
    color: white;
    padding: 3rem;
}
.callout--head{
    color: white;
    margin-top: 0;
    font-size: 3rem;
 
}
.callout .btn{
    justify-self: center;
   align-self: center;
 overflow: visible;
   box-sizing: border-box;
 
    }

.callout--content{
    text-align: center;
}

@media screen and (min-width:768px) {
    .callout .grid--1x2{
        grid-template-columns: 1fr auto;

    }
    .callout--content{
        text-align: left;
    }
    .callout .btn{
        justify-self:start;
        align-self: center;
        margin: 0 2rem;
    }
}

/* collapsible */

.collapsible--header{
 
    display: flex;
    justify-content:space-between;
   
}
.collapsible--heading{
    margin-top: 0;
    font-size: 3rem;
}
.chevron{ 
    transform: rotate(-90deg);
    transition: transform 0.3s;
}

.collapsible--content{
    max-height: 0;
overflow: hidden;
opacity: 0;
transition: all 0.5s;
}
.collapsible--expanded .chevron{
   
    transform: rotate(0);

}
.collapsible--expanded
.collapsible--content{
 max-height: 100vh;
 opacity: 1;
}

.block{
 --padding-vertical:6rem;
padding: var(--padding-vertical) 3rem;
}
.block__header{
    color: var(--color-headings);
    text-align: center;
    margin-bottom: 4rem;

}
.block__header p{
color:var(--color-body);
}
.block__heading{
    margin-top: 0;

}
.block--dark{
    background: #000;
    color: #7b858b;
}
.block--dark .block__header{
    color: white;
}
.block--skewed-right {
    padding-bottom: calc(var(--padding-vertical) + 4rem);
    clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0% 90%);
  }

.block--skewed-left {
    padding-bottom: calc(var(--padding-vertical) + 4rem);
    clip-path: polygon(0% 0%, 100% 0%, 100% 90%, 0% 100%);
  }

  .container {
    max-width: 1140px;
    margin: 0 auto;
  }

  /* navigation */

  .nav{
    background: #000;
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
    padding:  1rem;
    align-items: center;
  }

  .nav__list{
    width: 100%;
    margin: 0;
  }

  .nav__item{
    padding: 0.5rem 2rem;
    border: 1px solid #222;
  }
  
    .nav__item > a{
        color: #d2d0db;
        transition: color 0.3s;
    }
  
   
    .nav__item > a:hover{
        color: white;
      }

      .nav__toggler{
        opacity: 0.5;
        transition: box-shadow .15s;
        cursor: pointer;
    
      }

      .nav.collapsible--expanded .nav__toggler{
        opacity: 1;
        box-shadow: 0 0 0 3px #666;
        border-radius: 5px;
        
      }
.nav__brand{
    transform:translateY(5px);
}    

@media screen  and (min-width:768px){
 
    .nav__toggler{
        display: none;
    }
    .nav__list{
        width: auto;
        display: flex;
       font-size: 1.6rem;
       max-height: 100%;
       opacity: 1;
    }
    .nav__item{
        border: 0;
    }
    
}

/* Hero */



.block__tagline{
    font-size: 2rem;
    color: #b9c3cf;
    letter-spacing: 1px;
    margin: 2rem 0 5rem;
  }
.hero__image{
width: 100%;
}

@media screen and (min-width:768px) {
    .hero{
        padding-top: 0;
        
    }
   .hero .block__header{
        text-align: left;

        align-self: center;

    }

}

/* block domain */

.block--domain .input--group {
    box-shadow: 0 0 30px 20px #e6ebee;
    border: 0;
    margin:  4rem auto;
    max-width: 670px;
  justify-content: flex-end;
    
  }
  
  .block--prices {
    display: grid;
    color: var(--color-headings);
    grid-template-columns: repeat(2, 1fr);
    grid-template-rows: repeat(2, 6rem);
    font-size: 2rem;
    font-weight: 600;
    justify-items: center;
    max-width: 700px;
    margin: 0 auto;
  gap: 4rem;
    
    
  }
 
  
  @media screen and (min-width: 768px) {
    .block--prices {
      grid-template-columns: repeat(auto-fit, minmax(10rem, 1fr));

    }
  .block--domain  .block__header{
        text-align: center;
    }
  }
  

  /* block--plans */

  .block--plan .grid{
    gap: 8rem 4rem;
    box-sizing:unset;
  }
  .block--plan .card{
    max-width: 500px;
    margin: 0 auto;
    box-sizing: unset;
  }

  /* block--feature */

  .block--feature{
gap: 4rem 2rem;
margin: 12rem 0;

  }
  .block--feature .block__header{
    color: var(--color-headings);
  }
.feature__heading{
    margin: 1rem 0;
    color: var(--color-headings);
}
.block--feature:first-of-type{
    margin-top: 6rem;
}
  .feature__image{
    width: 100%;

  }

  @media screen and (min-width:768px) {
.block--feature:nth-of-type(even)
.feature__content
{
    order: 2;
}
.block--feature .block__header{
    text-align: center;
}
}

/* Showcaseblock */
.showcase .block__header{
    color: white;
}
.showcase--image >img{
    width: 100%;
}

@media screen and (min-width:768px) {
    .showcase .grid{
        grid-template-columns: repeat(2,50%);
    }
    .showcase--image{
        justify-self:end ;
    }
    .showcase .block__header{
        color: white;
    }
   
    .showcase--image >img{
    width: auto;
    justify-content: end;
    max-width: 700px;
    }
}

/* block footer */

.footer{
    background: #232323;
}
.footer a{
    color: #777;
    transition: color .3s;
}
.footer a:hover{
    color: white;
}

.footer--section{
    padding: 2rem;
    border-bottom: 1px solid #393939;
}
.footer--section .list{
    margin: 0;
}
.footer--heading{
    text-transform: uppercase;
    font-weight: 600;
    color: white;
}

.footer--brand{
    margin-top: 6rem;
    text-align: center;
}
.footer--brand img{
    width: 100%;
    max-width: 230px;
}
.footer--copyright{
    font-size: 2.1rem;
        color: white;
    opacity: 0.3;
}

@media screen and (min-width:768px) {

    .footer{
        font-size: 1.5rem;
       
    }
    .footer--sections {
        grid-template-columns: repeat(auto-fit, minmax(10rem, 1fr));
    
    }

    .footer .chevron{
        display: none;
    }

    .footer .collapsible--content{
        opacity: 1;
        max-height: 100%;
    }
    .footer--brand{
        order: -1;
        margin-top: 1rem;
    }
    .footer--copyright{
        font-size: 1.5rem;
    }
    .footer--section{
        border: 0;
    }
    .footer--heading{
        font-size: 1.5rem;
    }
}

/* Callout Signup */
.callout--signup{
    transform: translateY(5rem);
}
